"use strict";export class Products{Template(){return'\n        <div class="row no-gutters">\n            {{#each Categories}}\n                <div class="category-item">\n                    <div class="p-4"><h1>{{Title}}</h1></div>\n                    <div class="p-2">\n                        {{{Header}}}\n                    </div>\n                    <div class="row no-gutters p-1">\n                    {{#each Items}}\n                        <div class="col-md-12 item-container p-2">\n                            {{#if New}}<div class="is-new p-1 mr-2 float-right">New!</div>{{/if}}\n                            <div class="item-box row no-gutters" data-itemid="{{ID}}">\n                                <div class="col-12 text-left p-2 pl-3">\n                                    <h5 class="site-main-color">{{Title}}</h5>\n                                </div>\n                                <div class="col-3 p-2">\n                                    {{#if ImgPath}}\n                                    <img src="{{ImgPath}}" class="product-image"/>\n                                    {{/if}}\n                                </div>\n                                <div class="col-9 p-1 text-left">\n                                    <p>{{{Description}}}</p>\n                                </div>\n                                <div class="col-12 text-right">\n                                    {{#if DiscountPrice}}\n                                    <div><span class="pl-1 crossed">$ {{Price}}</span>$ {{DiscountPrice}}</div>\n                                    {{/if}}\n                                    {{#unless DiscountPrice}}\n                                    {{#if Price}}<div class="pr-2"><h5>$ {{Price}}</h5></div>{{/if}}\n                                    {{/unless}}\n                                    <div class="icons"></div>\n                                </div>\n                            </div>\n                        </div>\n                    {{/each}}\n                    </div>\n                    {{{Footer}}}\n                </div>\n            {{/each}}\n        </div>\n        '}Init(elements){return elements.Categories.forEach(item=>{item.Items.forEach(x=>{""==x.ImgPath&&(x.ImgPath="/images/default-dish.png")})}),elements}Css(){return"\n\n            .item-text-box{\n                display: grid;\n                grid-template-columns: auto;\n                grid-template-rows: 30% 70%;\n            }\n            .crossed {\n                text-decoration:line-through;\n            }\n            .item-box-text{\n                // white-space: nowrap;\n\t            // overflow: hidden;\n            }\n            .is-new{\n                color: white;\n                font-weight: bolder;\n                position: absolute;\n                width: 100px;\n                z-index: 1;\n                background-color: #F15B3F;\n                right:10px;\n                top:15px;\n            }\n            .category-item {\n                //background-color:#EEB78A;\n            }\n            .item-container .item-box {\n                // border : 1px solid gray;\n                // box-shadow: 0px 0px 12px 2px gray;\n                // height:200px;\n                //background-color:white;\n                border-bottom:solid #C91E00 1px;\n            }\n            .item-container .item-box:hover{\n                cursor:pointer;\n            }\n            .product-image{\n                height:100px;\n                width:100%;\n            }\n            .menu{\n                background-color:#F7F7F7;\n            }\n        "}}